import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import html5 from "../../img/html5.png";
import css from "../../img/css.png";
import sass from "../../img/sass.png";
import js from "../../img/js.png";
import react from "../../img/react.png";

const HomeConts3 = () => {
  const stackData = [
    {
      imageUrl: html5,
      title: "HTML5",
      subtext:
        "웹접근성, 표준성을 준수하며 시멘틱태그를 이용해 마크업 할 수 있습니다.",
      percent: 90,
    },
    {
      imageUrl: css,
      title: "CSS",
      subtext:
        "position, display를 적절히 사용하여 반응형웹을 적용할 수 있습니다.",
      percent: 85,
    },
    {
      imageUrl: sass,
      title: "SASS",
      subtext:
        "SCSS의 문법과 모듈화를 잘 활용하여 스타일 작업을 원활히 할 수 있습니다.",
      percent: 80,
    },
    {
      imageUrl: js,
      title: "JavaScript",
      subtext:
        "Javascript의 배열, 메소드, DOM을 이해하며 다양한 상황에 적용할 수 있습니다.",
      percent: 75,
    },
    {
      imageUrl: react,
      title: "React",
      subtext:
        "React 구조와 여러가지 hook들을 잘 알고 있으며, 범용적이고 재사용가능한 컴포넌트 설계를 할 수 있습니다.",
      percent: 70,
    },
  ];

  const drawChart = (canvas, percent) => {
    const ctx = canvas.getContext("2d");
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 40;
    const thickness = 10; // 도넛의 두께

    const startAngle = -Math.PI / 2;
    const endAngle = startAngle + (percent / 100) * 2 * Math.PI;

    // Clear the canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw the background circle
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
    ctx.fillStyle = "#e0e0e0";
    ctx.fill();

    // Draw the filled portion
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, startAngle, endAngle);
    ctx.lineWidth = thickness;
    ctx.strokeStyle = "#000"; // 내부 원형의 배경색
    ctx.stroke();
  };

  useEffect(() => {
    stackData.forEach((data, index) => {
      const canvas = document.getElementById(`chart-${index}`);
      if (canvas) {
        drawChart(canvas, data.percent);
      }
    });
  }, []);

  return (
    <FullpageSection>
      <div className="homeWrapper3">
        <h1 className="title">
          MY <span>SKILLS.</span>
        </h1>
        <ul className="stacksList">
          {stackData.map((data, index) => (
            <div key={index}>
              <li>
                <img src={data.imageUrl} alt={data.title} />
              </li>
              <div className="titComtainer">
                <h1>{data.title}</h1>
                <p>{data.subtext}</p>
              </div>
              <div className="skillPercent">
                {/* Canvas for the chart */}
                <canvas
                  id={`chart-${index}`}
                  width="100"
                  height="100"
                  style={{
                    width: "100px",
                    height: "100px",
                    borderRadius: "50%",
                  }}
                ></canvas>
                <p>{`${data.title} ${data.percent}%`}</p>
              </div>
            </div>
          ))}
        </ul>
      </div>
    </FullpageSection>
  );
};

export default HomeConts3;
