import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";

const HomeConts3 = () => {
  useEffect(() => {
    // Swiper 초기화
    const swiperInstance = new Swiper(".swiper-container", {
      direction: "horizontal",
      centeredSlides: true,
      slidesPerView: "auto",
      spaceBetween: 30,
      loop: true,
      effect: "flip",
      flipEffect: {
        rotate: 30,
        slideShadows: false,
      },
      initialSlide: 0,
    });

    // swiperInstance를 함수 내부에서 참조할 수 있도록
    // 함수 외부에서 정의한 변수에 할당
    window.swiperInstance = swiperInstance;

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiperInstance.destroy();
    };
  }, []);

  const handleNavButtonClick = (slideIndex) => {
    // 네비게이션 버튼 클릭 시 해당 슬라이드로 이동
    window.swiperInstance.slideTo(slideIndex);
  };

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <h1>Slide 1</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 2</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 3</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 4</h1>
            </div>
          </div>
          <div className="nav-buttons">
            <button onClick={() => handleNavButtonClick(1)}>Slide 1</button>
            <button onClick={() => handleNavButtonClick(2)}>Slide 2</button>
            <button onClick={() => handleNavButtonClick(3)}>Slide 3</button>
            <button onClick={() => handleNavButtonClick(4)}>Slide 4</button>
          </div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
