import React, { useState, useRef, useEffect } from "react";
import poter from "../../img/poter1.png";
import poter2 from "../../img/poter2.png";
import poter3 from "../../img/poter3.png";
import poter4 from "../../img/poter4.png";

const SlideItme = ({ text }) => {
  const imageData = [
    { imageUrl: poter, title: "poter1" },
    { imageUrl: poter2, title: "poter2" },
    { imageUrl: poter3, title: "poter3" },
    { imageUrl: poter4, title: "poter4" },
  ];

  const [selectedImage, setSelectedImage] = useState(poter);
  const [isPopupOpen, setPopupOpen] = useState(false);
  const popupRef = useRef(null);

  const handleSubImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const openPopup = () => {
    setPopupOpen(true);
  };

  const closePopup = () => {
    setPopupOpen(false);
  };

  const handleOutsideClick = (e) => {
    if (popupRef.current && !popupRef.current.contains(e.target)) {
      closePopup();
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleOutsideClick);

    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);
    };
  }, []);

  return (
    <div className="slideWrapper">
      <h1 className="title">{text}</h1>
      <div className="conArea">
        <div className="imgCon">
          <div className="mainImg" onClick={openPopup}>
            <img src={selectedImage} alt="포터"></img>
          </div>
          <div className="subImg">
            <ul>
              {imageData.map((data, index) => (
                <li key={index} onClick={() => handleSubImageClick(data.imageUrl)}>
                  <img src={data.imageUrl} alt={data.title}></img>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="linkBtnWrapper">
        <button>안녕</button>
      </div>

      {isPopupOpen && (
        <div className="popup" ref={popupRef}>
          <div className="popupContent">
            <img src={selectedImage} alt="포터"></img>
            <button onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SlideItme;
