import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
Swiper
import "swiper/swiper-bundle.css";
import Swiper from "swiper";

const HomeConts3 = () => {
  useEffect(() => {
    // Swiper 초기화
    const swiper = new Swiper(".swiper-container", {
      // Swiper 옵션 설정
      direction: "horizontal", // 슬라이드 방향 설정
      centeredSlides: true,
      slidesPerView: "auto",
      spaceBetween: 30,

      loop: true, // 무한 순환 설정
      initialSlide: 0, // 처음에 가운데 표시될 슬라이드 인덱스
    });

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiper.destroy();
    };
  }, []);

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <h1>Slide 1</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 2</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 3</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 4</h1>
            </div>
            {/* 원하는 만큼 슬라이드 아이템 추가 */}
          </div>
          {/* 이전/다음 버튼 */}
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
