// HomeConts3.js
import React, { useState } from "react";
import { FullpageSection } from "@ap.cx/react-fullpage";
import poter1 from "../../img/poter1.png";
import poter2 from "../../img/poter2.png";
import poter3 from "../../img/poter3.png";
import poter4 from "../../img/poter4.png";

const HomeConts3 = () => {
  const imageList = [
    { image: poter1, link: "https://github.com/kws04181/GreenComputerStudy", buttonText: "깃허브" },
    { image: poter2, link: "https://oasis-lily-c04.notion.site/Porter-69d0bdf883864b31b5d98c27f6b1834d", buttonText: "블로그" },
    { image: poter3, link: "https://elegant-clafoutis-9867b1.netlify.app/", buttonText: "사이트" },
    // "링크 4" 객체는 삭제했습니다.
  ];

  const [selectedImage, setSelectedImage] = useState(imageList[0].image);

  const handleSubImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  return (
    <FullpageSection>
      <div className={`homeWrapper3 homeBg`}>
        <div className="projectWrapperLeft">
          <div className="projectRightCon">
            <h1 className="projectText">PROJECTS</h1>
            <p>POTER - 리뉴얼</p>
            <p>한줄 소개 : 포터 코리아 웹페이지</p>
            <p>진행 기간 : 2023. 04 ~ 2023. 05</p>
            <p>Skills: HTML, CSS, JS</p>
            <div className="subImg">
              <ul>
                {imageList.map((item, index) => (
                  <li key={index}>
                    <img
                      src={item.image}
                      alt={`Poter ${index + 1}`}
                      onClick={() => handleSubImageClick(item.image)}
                    />
                  </li>
                ))}
              </ul>
            </div>
            <div className="btnCon">
              {imageList.map((item, index) => (
                <a
                  key={index}
                  href={item.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="button-link"
                >
                  <button className="button">{item.buttonText}</button>
                </a>
              ))}
            </div>
          </div>
          <div className="projectLeftCon"></div>
        </div>
        <div className="projectWrapperRight">
          <div
            className="projectBg"
            style={{
              background: `wheat url("${selectedImage}") no-repeat center/100%`,
            }}
          />
        </div>
      </div>
    </FullpageSection>
  );
};

export default HomeConts3;
