// SlideItme1.jsx
import React, { useState } from "react";

const SlideItme1 = ({ text, images }) => {
  const [selectedImage, setSelectedImage] = useState(images[0]);

  const handleSubImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  return (
    <div className="slideWrapper">
      <h1 className="title">{text}</h1>
      <div className="conArea">
        <div className="imgCon">
          <div className="mainImg">
            <img src={selectedImage} alt={text}></img>
          </div>
          <div className="subImg">
            <ul>
              {images.map((imageUrl, index) => (
                <li
                  key={index}
                  onClick={() => handleSubImageClick(imageUrl)}
                >
                  <img src={imageUrl} alt={`${text} ${index + 1}`}></img>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="linkBtnWrapper">
        <button>안녕</button>
      </div>
    </div>
  );
};

export default SlideItme1;
