import React, { useEffect, useState } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";

const HomeConts1 = () => {
  const profileData = [
    { name: "NAME :", text: "김성원" },
    { name: "BIRTH :", text: "1997.11.09" },
    { name: "PHONE :", text: "010-5752-3539" },
    { name: "MAIL :", text: "kws04182@gmail.com" },
  ];

  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const textWrapperRight = document.querySelector(".textWrapperRight");

    const handleMouseEnter = () => {
      setIsHovered(true);
    };

    const handleMouseLeave = () => {
      setIsHovered(false);
    };

    if (textWrapperRight) {
      textWrapperRight.addEventListener("mouseenter", handleMouseEnter);
      textWrapperRight.addEventListener("mouseleave", handleMouseLeave);
    }

    return () => {
      if (textWrapperRight) {
        textWrapperRight.removeEventListener("mouseenter", handleMouseEnter);
        textWrapperRight.removeEventListener("mouseleave", handleMouseLeave);
      }
    };
  }, []); // 빈 의존성 배열은 컴포넌트가 마운트될 때 한 번만 효과가 실행됨

  return (
    <div>
      <FullpageSection>
        <div className="homeWrapper">
          <div className="textWrapperLeft">
            <div className="profileBg"></div>
            <div className="profileTextCon">
              {profileData.map((item, idx) => (
                <div key={idx} className="listCon">
                  <span className="profileLeft">{item.name}</span>
                  <span className="profileRight">{item.text}</span>
                </div>
              ))}
            </div>
          </div>
          <div className={`textWrapperRight ${isHovered ? "fade" : ""}`}></div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts1;
