import React from "react";
import ScrollContainer from "react-scroll-horizontal";

const Home = () => {
  return (
    <ScrollContainer style={{ width: "100vw", height: "100vh" }}>
      <div style={{ width: "100vw", height: "100vh", backgroundColor: "red" }}>
        <h1>Section1</h1>
      </div>
      <div style={{ width: "100vw", height: "100vh", backgroundColor: "blue" }}>
        <h1>Section2</h1>
      </div>
      <div
        style={{ width: "100vw", height: "100vh", backgroundColor: "green" }}
      >
        <h1>Section3</h1>
      </div>
      <div
        className="footer"
        style={{ width: "100vw", height: "100vh", backgroundColor: "yellow" }}
      >
        <h1>Section4</h1>
      </div>
    </ScrollContainer>
  );
};

export default Home;

