// HomeConts3.js
import React, { useState } from "react";
import { FullpageSection } from "@ap.cx/react-fullpage";
import poter from "../../img/poter1.png";
import poter2 from "../../img/poter2.png";
import poter3 from "../../img/poter3.png";

const HomeConts3 = () => {
  const [selectedImage, setSelectedImage] = useState(poter);

  const handleSubImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  // JavaScript로 슬라이드 구현
  const slideContainerRef = useRef(null);

  useEffect(() => {
    const slideContainer = slideContainerRef.current;
    let currentIndex = 0;

    const images = [poter, poter2, poter3];

    const showImage = (index) => {
      const imageUrl = images[index];
      setSelectedImage(imageUrl);
    };

    const nextSlide = () => {
      currentIndex = (currentIndex + 1) % images.length;
      showImage(currentIndex);
    };

    const prevSlide = () => {
      currentIndex = (currentIndex - 1 + images.length) % images.length;
      showImage(currentIndex);
    };

    slideContainer.addEventListener("click", nextSlide);

    return () => {
      slideContainer.removeEventListener("click", nextSlide);
    };
  }, []);

  return (
    <FullpageSection>
      <div className={`homeWrapper3 homeBg`}>
        <div className="projectWrapperLeft">
          <div className="projectRightCon">
            <h1 className="projectText">PROJECTS</h1>
            <p>POTER - 리뉴얼</p>
            <p>한줄 소개 : 포터 코리아 웹페이지</p>
            <p>진행 기간 : 2023. 04 ~ 2023. 05</p>
            <p>Skills: HTML, CSS, JS</p>
            <div className="subImg" ref={slideContainerRef}>
              <ul>
                <li>
                  <img
                    src={poter}
                    alt="Poter 1"
                    onClick={() => handleSubImageClick(poter)}
                  />
                </li>
                <li>
                  <img
                    src={poter2}
                    alt="Poter 2"
                    onClick={() => handleSubImageClick(poter2)}
                  />
                </li>
                <li>
                  <img
                    src={poter3}
                    alt="Poter 3"
                    onClick={() => handleSubImageClick(poter3)}
                  />
                </li>
              </ul>
            </div>
            <div className="btnCon">
              <button>깃허브</button>
              <button>블로그</button>
              <button>사이트</button>
            </div>
          </div>
          <div className="projectLeftCon"></div>
        </div>
        <div className="projectWrapperRight">
          <div
            className="projectBg"
            style={{
              background: `wheat url("${selectedImage}") no-repeat center/100%`,
            }}
          />
        </div>
      </div>
    </FullpageSection>
  );
};

export default HomeConts3;
