import React, { useState, useEffect } from "react";
import "./App.css";

const Section = ({ color, content }) => {
  const sectionStyle = {
    backgroundColor: color,
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  return (
    <div style={sectionStyle}>
      <h3>{content}</h3>
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const handleScroll = () => {
      const scrolledHeight = window.scrollY;
      const pageHeight = window.innerHeight;

      const newPage = Math.ceil((scrolledHeight + pageHeight / 2) / pageHeight);
      setCurrentPage(newPage);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Section color="#282c34" content="Slide down!" />
      <Section color="#ff5f45" content="Keep going!" />
      <Section color="#0798ec" content="Slide up!" />
    </>
  );
};

export default App;
