import React, { useEffect } from 'react';
import { Element, scroller } from 'react-scroll';
import './App.css';

const App = () => {
  useEffect(() => {
    const handleScroll = (event) => {
      if (event.deltaY > 0) {
        // 스크롤 내릴 때
        scroller.scrollTo(`section${getNextSectionIndex()}`, {
          duration: 800,
          delay: 0,
          smooth: 'easeInOutQuart',
        });
      } else {
        // 스크롤 올릴 때
        scroller.scrollTo(`section${getPrevSectionIndex()}`, {
          duration: 800,
          delay: 0,
          smooth: 'easeInOutQuart',
        });
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  const getNextSectionIndex = () => {
    const currentSection = getCurrentSectionIndex();
    return currentSection < 3 ? currentSection + 1 : currentSection;
  };

  const getPrevSectionIndex = () => {
    const currentSection = getCurrentSectionIndex();
    return currentSection > 1 ? currentSection - 1 : currentSection;
  };

  const getCurrentSectionIndex = () => {
    const scrollPosition = window.scrollY;
    const windowHeight = window.innerHeight;
    return Math.ceil(scrollPosition / windowHeight);
  };

  return (
    <div className="app">
      <Element name="section1" className="section">
        <h1>Section 1</h1>
      </Element>

      <Element name="section2" className="section">
        <h1>Section 2</h1>
      </Element>

      <Element name="section3" className="section">
        <h1>Section 3</h1>
      </Element>
    </div>
  );
};

export default App;
