import React, { useEffect, useRef } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";
import SlideItme from "./SlideItme";
import SlideBtn from "./SlideBtn";

const HomeConts3 = () => {
  const swiperRef = useRef(null);

  useEffect(() => {
    // Swiper 초기화
    swiperRef.current = new Swiper(".swiper-container", {
      direction: "horizontal",
      centeredSlides: true,
      slidesPerView: "auto",
      spaceBetween: 30,
      loop: true,
      effect: "flip",
      flipEffect: {
        rotate: 30,
        slideShadows: false,
      },
      initialSlide: 0,
    });

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiperRef.current.destroy();
    };
  }, []);

  const handleSlideButtonClick = () => {
    // SlideBtn 클릭 시 현재 슬라이드를 숨김
    const currentSlide = swiperRef.current.activeIndex;
    document.querySelector(`.swiper-slide[data-swiper-slide-index="${currentSlide}"]`).style.opacity = 0;

    // 페이드아웃 효과를 위해 setTimeout을 사용
    setTimeout(() => {
      // 슬라이드 변경
      swiperRef.current.slideNext();

      // SlideBtn 클릭 후 새로운 현재 슬라이드를 페이드인
      const newCurrentSlide = swiperRef.current.activeIndex;
      document.querySelector(`.swiper-slide[data-swiper-slide-index="${newCurrentSlide}"]`).style.opacity = 1;
    }, 500); // 500ms 딜레이를 통해 페이드 아웃 후 슬라이드 변경 및 페이드 인
  };

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
          </div>
          <SlideBtn onButtonClick={handleSlideButtonClick} />
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
