import React, { useState, useEffect } from "react";
import poter from "../../img/poter1.png";
import poter2 from "../../img/poter2.png";
import poter3 from "../../img/poter3.png";
import poter4 from "../../img/poter4.png";
import zoomIcon from "../../img/zoom.png";

const SlideItem = ({ text }) => {
  const imageData = [
    { imageUrl: poter, title: "poter1" },
    { imageUrl: poter2, title: "poter2" },
    { imageUrl: poter3, title: "poter3" },
    { imageUrl: poter4, title: "poter4" },
  ];

  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const handleOutsideClick = (e) => {
    if (isPopupOpen && !e.target.closest(".popup")) {
      setIsPopupOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleOutsideClick);

    if (isPopupOpen) {
      document.body.classList.add("popup-open");
    } else {
      document.body.classList.remove("popup-open");
    }

    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);
      document.body.classList.remove("popup-open");
    };
  }, [isPopupOpen]);

  return (
    <div className="slideWrapper">
      <h1 className="title">{text}</h1>
      <div className="conArea">
        <div className="imgCon">
          <div
            className="mainImg"
            onClick={() => setIsPopupOpen(true)}
            onMouseEnter={(e) => {
              e.currentTarget.style.filter = "brightness(80%)";
              e.currentTarget.querySelector("::before").style.opacity = 1;
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.filter = "brightness(100%)";
              e.currentTarget.querySelector("::before").style.opacity = 0;
            }}
          >
            <img src={poter} alt="포터" />
            <div
              className="zoomIcon"
              style={{
                position: "absolute",
                right: "15px",
                bottom: "13px",
                width: "22px",
                height: "22px",
                background: `url(${zoomIcon}) 50% 50% no-repeat`,
                backgroundSize: "22px 22px",
                color: "white",
                fontWeight: 300,
                opacity: 0,
                filter: "invert(100%) drop-shadow(0 0 4px rgba(0, 0, 0, 0.9))",
                zIndex: 10,
                transition: "200ms ease",
              }}
            />
          </div>
          {isPopupOpen && (
            <div className="popup">
              <button className="closeBtn" onClick={() => setIsPopupOpen(false)}>
                닫기
              </button>
              <div className="popupContent">
                <img src={poter} alt="포터" />
              </div>
            </div>
          )}
          <div className="subImg">
            <ul>
              {imageData.map((item, idx) => (
                <li
                  key={idx}
                  onClick={() => setIsPopupOpen(true)}
                  onMouseEnter={(e) => (e.currentTarget.style.filter = "brightness(80%)")}
                  onMouseLeave={(e) => (e.currentTarget.style.filter = "brightness(100%)")}
                >
                  <img src={item.imageUrl} alt={item.title} />
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="linkBtnWrapper">
        <button>안녕</button>
      </div>
    </div>
  );
};

export default SlideItem;
