import React, { useEffect, useState } from "react";
import HomeConts1 from "./HomeConts1";
import HomeConts2 from "./HomeConts2";
import HomeConts3 from "./HomeConts3";
import HomeConts4 from "./HomeConts4";

const FullpageScroll = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const [sectionOffsets, setSectionOffsets] = useState([]);

  useEffect(() => {
    const sections = document.querySelectorAll(".fullpage-section");
    const offsets = Array.from(sections).map((section) => section.offsetTop);
    setSectionOffsets(offsets);

    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const newCurrentSection = offsets.findIndex((offset) => scrollPosition < offset);
      setCurrentSection(newCurrentSection === -1 ? offsets.length - 1 : newCurrentSection);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleSectionClick = (sectionIndex) => {
    setCurrentSection(sectionIndex);
    window.scrollTo({ top: sectionOffsets[sectionIndex], behavior: "smooth" });
  };

  return (
    <div className="fullpage-scroll-container">
      <div className={`fullpage-section ${currentSection === 0 ? "active" : ""}`}>
        <HomeConts1 onClick={() => handleSectionClick(1)} />
      </div>
      <div className={`fullpage-section ${currentSection === 1 ? "active" : ""}`}>
        <HomeConts2 onClick={() => handleSectionClick(2)} />
      </div>
      <div className={`fullpage-section ${currentSection === 2 ? "active" : ""}`}>
        <HomeConts3 onClick={() => handleSectionClick(3)} />
      </div>
      <div className={`fullpage-section ${currentSection === 3 ? "active" : ""}`}>
        <HomeConts4 onClick={() => handleSectionClick(0)} />
      </div>
    </div>
  );
};

export default FullpageScroll;
