import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";

const HomeConts3 = () => {
  useEffect(() => {
    // Swiper 초기화
    const swiperInstance = new Swiper(".swiper-container", {
      direction: "horizontal",
      centeredSlides: true,
      slidesPerView: "auto",
      spaceBetween: 30,
      loop: true,
      effect: "flip",
      flipEffect: {
        rotate: 30,
        slideShadows: false,
      },
      initialSlide: 0,
    });

    // swiperInstance를 함수 내부에서 참조할 수 있도록
    // 함수 외부에서 정의한 변수에 할당
    window.swiperInstance = swiperInstance;

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiperInstance.destroy();
    };
  }, []);

  const handleNextButtonNext = () => {
    // 다음 슬라이드로 이동
    window.swiperInstance.slideNext();
  };
  const handleNextButtonPrev = () => {
    // 다음 슬라이드로 이동
    window.swiperInstance.slidePrev();
  };

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <h1>Slide 2</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 3</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 4</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 1</h1>
            </div>
          </div>
          <div className="nav-button">
            <button onClick={handleNextButtonPrev}>
              <span class="material-symbols-outlined">
                keyboard_double_arrow_left
              </span>
            </button>
            <button onClick={handleNextButtonNext}>
              <span class="material-symbols-outlined">double_arrow</span>
            </button>
          </div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
