import React, { useEffect, useState } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";

const HomeConts1 = () => {
  const [firstText, setFirstText] = useState("");
  const [secondText, setSecondText] = useState("");
  const firstTextPhrases = ["안녕하세요:)"];
  const secondTextPhrases = ["주니어 Frontend 개발자 김성원입니다."];

  useEffect(() => {
    let firstIndex = 0;
    let secondIndex = 0;

    const updateText = () => {
      if (firstIndex < firstTextPhrases[0].length) {
        setFirstText((prev) => prev + firstTextPhrases[0][firstIndex]);
        firstIndex++;
      } else if (secondIndex < secondTextPhrases[0].length) {
        setSecondText((prev) => prev + secondTextPhrases[0][secondIndex]);
        secondIndex++;
      }
    };

    const intervalId = setInterval(updateText, 80);

    return () => clearInterval(intervalId);
  }, [firstTextPhrases, secondTextPhrases]);

  return (
    <div>
      <FullpageSection>
        <div className="homeWrapper">
          <div className="textWrapper">
            <h1 className="firstTxt">{firstText}</h1>
            <h1 className="secondTxt">{secondText}</h1>
            <p>저는 원래 토목과를 전공하였는데요.</p>
            <p>우연한 기회로 웹디자인과 프로그래밍 기초언어를 접하게 되었고,</p>
            <p>컴퓨터 공학에 관심을 갖게 되었습니다.</p>
            <p>개발을 공부하다보니 많은 사람들이 애용하는 서비스를</p>
            <p>직접 개발하고 싶다는 생각을 하게 되었고,</p>
            <p>자연스럽게 프론트엔드 개발자를 꿈꾸게 되었습니다.</p>
          </div>
          <div className="imgWrapper"></div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts1;
