import React, { useEffect, useRef } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";

const HomeConts1 = () => {
  const textWrapperRightRef = useRef(null);

  useEffect(() => {
    const handlePageChange = (pageNumber) => {
      if (pageNumber === 1) {
        // 페이지가 HomeConts1로 전환될 때 애니메이션 추가
        if (textWrapperRightRef.current) {
          textWrapperRightRef.current.classList.add("fade-in-out");
        }
      } else {
        // 다른 페이지로 이동할 때 애니메이션 제거
        if (textWrapperRightRef.current) {
          textWrapperRightRef.current.classList.remove("fade-in-out");
        }
      }
    };

    // Fullpage 컴포넌트에서 페이지 전환 시 호출되는 콜백 등록
    Fullpage.on("afterChange", handlePageChange);

    // 컴포넌트 언마운트 시 콜백 제거 (clean-up)
    return () => {
      Fullpage.off("afterChange", handlePageChange);
    };
  }, []); // useEffect는 한 번만 실행되도록 빈 배열을 전달

  return (
    <div>
      <FullpageSection>
        <div className="homeWrapper">
          <div className="textWrapperLeft">
            <div className="profileBg"></div>
            <div className="profileTextCon">
              {/* 생략 */}
            </div>
          </div>
          <div
            ref={textWrapperRightRef}
            className="textWrapperRight"
          ></div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts1;
