import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";
import SlideItme from "./SlideItme";
import SlideBtn from "./SlideBtn";

const HomeConts3 = () => {
  useEffect(() => {
    // Swiper 초기화
    const swiperInstance = new Swiper(".swiper-container", {
      direction: "horizontal",
      centeredSlides: true,
      slidesPerView: 1, // 한 번에 하나의 슬라이드만 보이도록
      spaceBetween: 0, // 슬라이드 간 간격 없애기
      loop: true,
      effect: "fade", // 페이드 효과 사용
      fadeEffect: {
        crossFade: true, // 크로스-페이드 활성화
      },
      initialSlide: 0,
    });

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiperInstance.destroy();
    };
  }, []);

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
            <div className="swiper-slide">
              <SlideItme text="PROJECT" />
            </div>
          </div>
          <SlideBtn />
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
