import React, { useEffect, useRef } from "react";
import HomeConts1 from "../components/home/HomeConts1";
import HomeConts2 from "../components/home/HomeConts2";
import HomeConts3 from "../components/home/HomeConts3";
import HomeConts4 from "../components/home/HomeConts4";

const Home = () => {
  const Conts1Ref = useRef(null);
  const Conts2Ref = useRef(null);
  const Conts3Ref = useRef(null);
  const Conts4Ref = useRef(null);

  useEffect(() => {
    if (Conts1Ref.current) {
      console.log(Conts1Ref.current.getBoundingClientRect().top);
    }
    if (Conts2Ref.current) {
      console.log(Conts2Ref.current.getBoundingClientRect().top);
    }
    if (Conts3Ref.current) {
      console.log(Conts3Ref.current.getBoundingClientRect().top);
    }
    if (Conts4Ref.current) {
      console.log(Conts4Ref.current.getBoundingClientRect().top);
    }
  }, []);

  return (
    <>
      <HomeConts1 ref={Conts1Ref} />
      <HomeConts2 ref={Conts2Ref} />
      <HomeConts3 ref={Conts3Ref} />
      <HomeConts4 ref={Conts4Ref} />
    </>
  );
};

export default Home;

