import React, { useRef } from "react";
import { NavLink } from "react-router-dom";
<link
  href="https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;500;600;700;800&display=swap"
  rel="stylesheet"
></link>;

const Header = () => {
  const line = useRef(null);

  const lineMove = (e) => {
    let elLeft = e.target.getBoundingClientRect().left;

    console.log(line.getBoundingClientRect());
    line.getBoundingClientRect().left = elLeft;
  };

  return (
    <header>
      <div className="inner">
        <ul className="gnb">
          <li className="line" ref={line}></li>
          <li>
            <NavLink to="/">Home</NavLink>
          </li>
          <li>
            <NavLink to="/about">About</NavLink>
          </li>

          <li>
            <NavLink to="/projects">Projects</NavLink>
          </li>
          <li>
            <NavLink to="/contact">Contact</NavLink>
          </li>
        </ul>
        <div className="totalMenuBtn"></div>
        <ul className="totalMenuConts">
          <li>
            <NavLink to="/">Home</NavLink>
          </li>
          <li>
            <NavLink to="/about">About</NavLink>
          </li>

          <li>
            <NavLink to="/projects">Projects</NavLink>
          </li>
          <li>
            <NavLink to="/contact">Contact</NavLink>
          </li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
