import React, { useEffect, useRef } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import html5 from "../../img/html5.png";
import css from "../../img/css.png";
import sass from "../../img/sass.png";
import js from "../../img/js.png";
import react from "../../img/react.png";

const HomeConts3 = () => {
  const stackData = [
    {
      imageUrl: html5,
      title: "HTML5",
      subtext:
        "웹접근성, 표준성을 준수하며 시멘틱태그를 이용해 마크업 할 수 있습니다.",
      percent: 95,
    },
    {
      imageUrl: css,
      title: "CSS",
      subtext:
        "position, display를 적절히 사용하여 반응형웹을 적용할 수 있습니다.",
      percent: 90,
    },
    {
      imageUrl: sass,
      title: "SASS",
      subtext:
        "SCSS의 문법과 모듈화를 잘 활용하여 스타일 작업을 원활히 할 수 있습니다.",
      percent: 80,
    },
    {
      imageUrl: js,
      title: "JavaScript",
      subtext:
        "Javascript의 배열, 메소드, DOM을 이해하며 다양한 상황에 적용할 수 있습니다.",
      percent: 70,
    },
    {
      imageUrl: react,
      title: "React",
      subtext:
        "React 구조와 여러가지 hook들을 잘 알고 있으며, 범용적이고 재사용가능한 컴포넌트 설계를 할 수 있습니다.",
      percent: 70,
    },
  ];

  const drawChart = (ctx, centerX, centerY, radius, startAngle, endAngle, color) => {
    // Draw the background circle
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
    ctx.fillStyle = "#e0e0e0";
    ctx.fill();

    // Draw the filled portion
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, startAngle, endAngle);
    ctx.lineTo(centerX, centerY);
    ctx.fillStyle = color;
    ctx.fill();
  };

  const drawCharts = (canvas) => {
    const ctx = canvas.getContext("2d");
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 40;

    let startAngle = -Math.PI / 2;

    stackData.forEach((data) => {
      const endAngle = startAngle + (data.percent / 100) * 2 * Math.PI;
      drawChart(ctx, centerX, centerY, radius, startAngle, endAngle, "#3498db");
      startAngle = endAngle;
    });
  };

  useEffect(() => {
    const canvas = document.getElementById("combined-chart");
    if (canvas) {
      drawCharts(canvas);
    }
  }, []);

  return (
    <FullpageSection>
      <div className="homeWrapper3">
        <h1 className="title">
          나의 <span>기술.</span>
        </h1>
        <ul className="stacksList">
          <li>
            <div className="titComtainer">
              <h1>전체 기술</h1>
              <p>전체 기술의 원형 차트입니다.</p>
            </div>
            <div className="skillPercent">
              {/* Canvas for the combined chart */}
              <canvas
                id="combined-chart"
                width="80"
                height="80"
                style={{
                  width: "80px",
                  height: "80px",
                  borderRadius: "50%",
                }}
              ></canvas>
            </div>
          </li>
        </ul>
      </div>
    </FullpageSection>
  );
};

export default HomeConts3;
