import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";

const HomeConts3 = () => {
  useEffect(() => {
    // Swiper 초기화
    const swiper = new Swiper(".swiper-container", {
      // Swiper 옵션 설정
      direction: "horizontal", // 슬라이드 방향 설정
      centeredSlides: true,
      slidesPerView: "auto",
      spaceBetween: 30,

      loop: true, // 무한 순환 설정
      effect: "flip", // 플립 효과 설정
      flipEffect: {
        rotate: 30, // 플립 시 회전 각도
        slideShadows: false, // 슬라이드 그림자 표시 여부
      },
      initialSlide: 0, // 처음에 가운데 표시될 슬라이드 인덱스
    });

    return () => {
      // 컴포넌트 언마운트 시 Swiper 파기
      swiper.destroy();
    };
  }, []);

  const handleNavButtonClick = (slideIndex) => {
    // 네비게이션 버튼 클릭 시 해당 슬라이드로 이동
    swiper.slideTo(slideIndex);
  };

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <h1>Slide 1</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 2</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 3</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 4</h1>
            </div>
            {/* 원하는 만큼 슬라이드 아이템 추가 */}
          </div>
        </div>


        <div className="nav-buttons">
        <button onClick={() => handleNavButtonClick(0)}>Slide 1</button>
        <button onClick={() => handleNavButtonClick(1)}>Slide 2</button>
        <button onClick={() => handleNavButtonClick(2)}>Slide 3</button>
        <button onClick={() => handleNavButtonClick(3)}>Slide 4</button>
      </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
