import React, { useState, useEffect } from "react";
import poter from "../../img/poter1.png";
import poter2 from "../../img/poter2.png";
import poter3 from "../../img/poter3.png";
import poter4 from "../../img/poter4.png";

const SlideItme = ({ text }) => {
  const imageData = [
    { imageUrl: poter, title: "poter1" },
    { imageUrl: poter2, title: "poter2" },
    { imageUrl: poter3, title: "poter3" },
    { imageUrl: poter4, title: "poter4" },
  ];

  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const handleOutsideClick = (e) => {
    if (isPopupOpen && !e.target.closest(".popup")) {
      setIsPopupOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleOutsideClick);

    // 팝업이 열렸을 때 body에 클래스 추가
    if (isPopupOpen) {
      document.body.classList.add("popup-open");
    } else {
      document.body.classList.remove("popup-open");
    }

    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);

      // 컴포넌트가 언마운트될 때 body 클래스 제거
      document.body.classList.remove("popup-open");
    };
  }, [isPopupOpen]);

  return (
    <div className="slideWrapper">
      <h1 className="title">{text}</h1>
      <div className="conArea">
        <div className="imgCon">
          <div className="mainImg" onClick={() => setIsPopupOpen(true)}>
            <img src={poter} alt="포터"></img>
          </div>
          {isPopupOpen && (
            <div className="popup">
              <button className="closeBtn" onClick={() => setIsPopupOpen(false)}>
                닫기
              </button>
              {/* 팝업 내용 */}
              <div className="popupContent">
                <img src={poter} alt="포터"></img>
              </div>
            </div>
          )}
          <div className="subImg">
            <ul>
              {imageData.map((item, idx) => (
                <li key={idx} onClick={() => setIsPopupOpen(true)}>
                  <img src={item.imageUrl} alt={item.title}></img>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="linkBtnWrapper">
        <button>안녕</button>
      </div>
    </div>
  );
};

export default SlideItme;
