import React, { useEffect } from "react";
import Fullpage, {
  FullPageSections,
  FullpageNavigation,
  FullpageSection,
} from "@ap.cx/react-fullpage";
import Swiper from "swiper";
import "swiper/swiper-bundle.css";

const HomeConts3 = () => {
  useEffect(() => {
    let swiperInstance;

    const initSwiper = () => {
      swiperInstance = new Swiper(".swiper-container", {
        direction: "horizontal",
        centeredSlides: true,
        slidesPerView: "auto",
        spaceBetween: 30,
        loop: true,
        effect: "flip",
        flipEffect: {
          rotate: 30,
          slideShadows: false,
        },
        initialSlide: 0,
      });

      swiperInstance.on("slideChange", handleSlideChange);
    };

    const handleSlideChange = () => {
      // 현재 활성화된 슬라이드의 인덱스 가져오기
      const activeIndex = swiperInstance.activeIndex;

      // 슬라이드의 인덱스에 따라 백그라운드 컬러 변경
      changeBackgroundColor(activeIndex);
    };

    const changeBackgroundColor = (slideIndex) => {
      const swiperWrapper = document.querySelector(".swiper-wrapper");

      // 슬라이드 인덱스에 따라 백그라운드 컬러 변경
      switch (slideIndex) {
        case 0:
          swiperWrapper.style.backgroundColor = "white";
          break;
        case 1:
          swiperWrapper.style.backgroundColor = "lightgray";
          break;
        case 2:
          swiperWrapper.style.backgroundColor = "lightblue";
          break;
        case 3:
          swiperWrapper.style.backgroundColor = "lightgreen";
          break;
        default:
          break;
      }
    };

    initSwiper();

    return () => {
      if (swiperInstance) {
        // 컴포넌트 언마운트 시 Swiper 파기
        swiperInstance.destroy();
      }
    };
  }, []);

  const handleNavButtonClick = (slideIndex) => {
    // 현재 활성화된 슬라이드의 인덱스 가져오기
    const activeIndex = swiperInstance.activeIndex;

    // 슬라이드의 인덱스에 따라 백그라운드 컬러 변경
    changeBackgroundColor(activeIndex);
  };

  const changeBackgroundColor = (slideIndex) => {
    const swiperWrapper = document.querySelector(".swiper-wrapper");

    // 슬라이드 인덱스에 따라 백그라운드 컬러 변경
    switch (slideIndex) {
      case 0:
        swiperWrapper.style.backgroundColor = "white";
        break;
      case 1:
        swiperWrapper.style.backgroundColor = "lightgray";
        break;
      case 2:
        swiperWrapper.style.backgroundColor = "lightblue";
        break;
      case 3:
        swiperWrapper.style.backgroundColor = "lightgreen";
        break;
      default:
        break;
    }
  };

  return (
    <div className="homeWrapper3">
      <FullpageSection>
        <div className="swiper-container">
          <div className="swiper-wrapper">
            {/* 슬라이드 아이템들 */}
            <div className="swiper-slide">
              <h1>Slide 2</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 3</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 4</h1>
            </div>
            <div className="swiper-slide">
              <h1>Slide 1</h1>
            </div>
          </div>
          <div className="nav-buttons">
            <button onClick={() => handleNavButtonClick(1)}></button>
            <button onClick={() => handleNavButtonClick(2)}></button>
            <button onClick={() => handleNavButtonClick(3)}></button>
            <button onClick={() => handleNavButtonClick(0)}></button>
          </div>
        </div>
      </FullpageSection>
    </div>
  );
};

export default HomeConts3;
